package dao;

import malgnsoft.db.DataObject;
import malgnsoft.db.DataSet;

public class {DAO_CLASS} extends DataObject {
  public {DAO_CLASS}() {
    this.table = "{TABLE_NAME}";
  }

  // 커스텀 조회 메소드
  public DataSet findByStatus(String status) {
    return this.find("WHERE status = ?", new Object[]{status});
  }

  // 커스텀 카운트 메소드
  public int countByStatus(String status) {
    return this.findCount("WHERE status = '" + status + "'");
  }

  // 복잡한 JOIN 조회
  public DataSet findWithUser() {
    return this.query(
      "SELECT a.*, b.name as user_name " +
      "FROM {TABLE_NAME} a " +
      "LEFT JOIN tb_user b ON a.user_id = b.id " +
      "ORDER BY a.id DESC"
    );
  }
}
